// shared/schema.ts
import { pgTable, text, serial, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
var users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull()
});
var insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true
});
var contactSubmissions = pgTable("contact_submissions", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  email: text("email").notNull(),
  phone: text("phone"),
  dogName: text("dog_name").notNull(),
  dogBreed: text("dog_breed"),
  service: text("service").notNull(),
  message: text("message"),
  createdAt: timestamp("created_at").defaultNow().notNull()
});
var contactFormSchema = createInsertSchema(contactSubmissions).omit({
  id: true,
  createdAt: true
});
var contactFormValidationSchema = contactFormSchema.extend({
  email: z.string().email("Bitte geben Sie eine g\xFCltige E-Mail-Adresse ein."),
  name: z.string().min(2, "Name muss mindestens 2 Zeichen lang sein."),
  dogName: z.string().min(1, "Bitte geben Sie den Namen Ihres Hundes an."),
  service: z.string().min(1, "Bitte w\xE4hlen Sie einen Service aus.")
});

// server/routes.ts
import { ZodError } from "zod";
import { fromZodError } from "zod-validation-error";
async function registerRoutes(app2) {
  const apiRouter = express.Router();
  app2.use("/api", apiRouter);
  apiRouter.post("/contact", async (req, res) => {
    try {
      const validatedData = contactFormValidationSchema.parse(req.body);
      const submission = await storage.submitContactForm(validatedData);
      return res.status(201).json({
        success: true,
        message: "Formular erfolgreich \xFCbermittelt. Wir werden uns in K\xFCrze bei Ihnen melden!",
        submission
      });
    } catch (error) {
      if (error instanceof ZodError) {
        const validationError = fromZodError(error);
        return res.status(400).json({
          success: false,
          message: "Validierungsfehler",
          errors: validationError.details
        });
      }
      console.error("Contact form submission error:", error);
      return res.status(500).json({
        success: false,
        message: "Ein Fehler ist aufgetreten. Bitte versuchen Sie es sp\xE4ter noch einmal."
      });
    }
  });
  apiRouter.get("/contact/submissions", async (req, res) => {
    try {
      const submissions = await storage.getAllSubmissions();
      return res.status(200).json({
        success: true,
        submissions
      });
    } catch (error) {
      console.error("Error retrieving submissions:", error);
      return res.status(500).json({
        success: false,
        message: "Fehler beim Abrufen der Einreichungen"
      });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

